import React, { useState } from 'react';
import axios from 'axios';
import './CaseSummary.css';

const CaseAnalyzer = () => {
  const [caseText, setCaseText] = useState('');
  const [caseCategory, setCaseCategory] = useState('');
  const [pendingYears, setPendingYears] = useState(0);
  const [summary, setSummary] = useState('');
  const [priority, setPriority] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleAnalyze = async () => {
    if (!caseText || !caseCategory) {
      setError('Please enter case details and category.');
      return;
    }

    setLoading(true);
    setError('');
    setSummary('');
    setPriority('');

    try {
      console.log('Sending request to backend...');
      const response = await axios.post('http://127.0.0.1:5000/add_case', {
        case_text: caseText,
        category: caseCategory,
        pending_years: pendingYears,
      });

      console.log('Response received:', response.data);
      setSummary(response.data.summary);
      setPriority(response.data.priority);
    } catch (error) {
      console.error('Error details:', error);
      if (error.response) {
        // The request was made and the server responded with a status code
        // that falls out of the range of 2xx
        setError(`Error: ${error.response.data.error || 'Server error occurred'}`);
      } else if (error.request) {
        // The request was made but no response was received
        setError('No response from server. Please check if the backend is running.');
      } else {
        // Something happened in setting up the request that triggered an Error
        setError(`Error: ${error.message}`);
      }
    } finally {
      setLoading(false);
    }
  };

  const getPriorityColor = (priority) => {
    switch (priority) {
      case 'High':
        return 'text-red-500';
      case 'Medium':
        return 'text-yellow-500';
      case 'Low':
        return 'text-green-500';
      default:
        return 'text-gray-500';
    }
  };

  return (
    <div className="max-w-4xl mx-auto bg-white rounded-lg shadow-md p-6">
      <div className="space-y-4">
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">
            Case Details
          </label>
          <textarea
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            rows="6"
            placeholder="Enter case details..."
            value={caseText}
            onChange={(e) => setCaseText(e.target.value)}
          />
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">
            Case Category
          </label>
          <input
            type="text"
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            placeholder="Enter case category (e.g., Civil, Criminal)"
            value={caseCategory}
            onChange={(e) => setCaseCategory(e.target.value)}
          />
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">
            Pending Years
          </label>
          <input
            type="number"
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            placeholder="Enter number of pending years"
            value={pendingYears}
            onChange={(e) => setPendingYears(parseInt(e.target.value) || 0)}
          />
        </div>

        {error && (
          <div className="p-4 bg-red-50 border border-red-200 rounded-md">
            <p className="text-red-600">{error}</p>
          </div>
        )}

        <button
          className={`w-full py-2 px-4 rounded-md text-white font-medium ${
            loading
              ? 'bg-blue-400 cursor-not-allowed'
              : 'bg-blue-500 hover:bg-blue-600'
          }`}
          onClick={handleAnalyze}
          disabled={loading}
        >
          {loading ? 'Analyzing...' : 'Analyze Case'}
        </button>

        {summary && (
          <div className="mt-6 p-6 bg-gray-50 rounded-lg prose prose-lg max-w-none">
            <div 
              className="case-summary"
              dangerouslySetInnerHTML={{ __html: summary }}
            />
          </div>
        )}

        {priority && (
          <div className="mt-4 p-4 bg-gray-50 rounded-lg">
            <h3 className="text-lg font-semibold text-gray-800 mb-2">Priority Level</h3>
            <p className={`text-xl font-bold ${getPriorityColor(priority)}`}>
              {priority} Priority
            </p>
          </div>
        )}
      </div>
    </div>
  );
};

export default CaseAnalyzer;
