import React, { useState, useEffect } from 'react';
import CaseTable from './components/CaseTable';
import CaseDetails from './components/CaseDetails';
import CaseAnalysis from './components/CaseAnalysis';
import AddCase from './components/AddCase';

function App() {
  const [selectedCase, setSelectedCase] = useState(null);
  const [caseStats, setCaseStats] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showAddCase, setShowAddCase] = useState(false);

  useEffect(() => {
    fetchCaseStats();
  }, []);

  const fetchCaseStats = async () => {
    try {
      const response = await fetch('http://127.0.0.1:5000/api/cases/stats');
      if (!response.ok) {
        throw new Error('Failed to fetch case statistics');
      }
      const data = await response.json();
      setCaseStats(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleCaseSelect = (caseData) => {
    setSelectedCase(caseData);
  };

  const handleCloseDetails = () => {
    setSelectedCase(null);
  };

  const handleCaseAdded = (newCase) => {
    fetchCaseStats(); // Refresh statistics
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-red-500">Error: {error}</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-100">
      <header className="bg-white shadow">
        <div className="max-w-7xl mx-auto py-6 px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center">
            <h1 className="text-3xl font-bold text-gray-900">Case Management System</h1>
            <button
              onClick={() => setShowAddCase(!showAddCase)}
              className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2"
            >
              {showAddCase ? 'Cancel' : 'Add New Case'}
            </button>
          </div>
        </div>
      </header>

      <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        {/* Case Analysis */}
        <div className="mb-8">
          <CaseAnalysis caseStats={caseStats} />
        </div>

        {/* Add Case Form */}
        {showAddCase && (
          <div className="mb-8">
            <AddCase onCaseAdded={handleCaseAdded} />
          </div>
        )}

        {/* Case Table */}
        <div className="bg-white shadow rounded-lg">
          <CaseTable onCaseSelect={handleCaseSelect} />
        </div>
      </main>

      {/* Case Details Modal */}
      {selectedCase && (
        <CaseDetails
          caseData={selectedCase}
          onClose={handleCloseDetails}
        />
      )}
    </div>
  );
}

export default App;
