import React, { useState } from 'react';
import axios from 'axios';
import './CaseSummary.css';

const CaseAnalyzer = ({ onCaseAdded }) => {
  const [caseText, setCaseText] = useState('');
  const [category, setCategory] = useState('Criminal');
  const [pendingYears, setPendingYears] = useState('');
  const [summary, setSummary] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const response = await axios.post('http://127.0.0.1:5000/add_case', {
        case_text: caseText,
        category: category,
        pending_years: parseFloat(pendingYears)
      });

      setSummary(response.data.summary);
      
      // Clear form
      setCaseText('');
      setCategory('Criminal');
      setPendingYears('');
      
      // Notify parent component to refresh case list
      if (onCaseAdded) {
        onCaseAdded();
      }
    } catch (err) {
      setError('Error adding case. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="bg-white rounded-lg shadow-md p-6">
      <h2 className="text-2xl font-bold text-gray-800 mb-6">Add New Case</h2>
      
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">
            Case Details
          </label>
          <textarea
            value={caseText}
            onChange={(e) => setCaseText(e.target.value)}
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            rows="6"
            required
            placeholder="Enter case details..."
          />
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">
            Category
          </label>
          <select
            value={category}
            onChange={(e) => setCategory(e.target.value)}
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            required
          >
            <option value="Criminal">Criminal</option>
            <option value="Civil">Civil</option>
            <option value="Family">Family</option>
            <option value="Property">Property</option>
          </select>
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">
            Pending Years
          </label>
          <input
            type="number"
            value={pendingYears}
            onChange={(e) => setPendingYears(e.target.value)}
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            min="0"
            step="0.1"
            required
            placeholder="Enter pending years..."
          />
        </div>

        {error && (
          <div className="text-red-500 text-sm">{error}</div>
        )}

        <button
          type="submit"
          disabled={loading}
          className={`w-full py-2 px-4 rounded-md text-white font-medium ${
            loading
              ? 'bg-blue-400 cursor-not-allowed'
              : 'bg-blue-600 hover:bg-blue-700'
          }`}
        >
          {loading ? 'Adding Case...' : 'Add Case'}
        </button>
      </form>

      {summary && (
        <div className="mt-6">
          <h3 className="text-lg font-semibold text-gray-800 mb-2">AI Summary</h3>
          <div 
            className="case-summary"
            dangerouslySetInnerHTML={{ __html: summary }}
          />
        </div>
      )}
    </div>
  );
};

export default CaseAnalyzer;
