import React, { useState } from 'react';
import CaseAnalyzer from './components/CaseAnalyzer';
import CaseStats from './components/CaseStats';
import CaseTable from './components/CaseTable';

function App() {
  const [refreshKey, setRefreshKey] = useState(0);

  const handleCaseAdded = () => {
    setRefreshKey(prev => prev + 1);
  };

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="container mx-auto px-4">
        <h1 className="text-3xl font-bold text-center text-gray-800 mb-8">
          Judiciary Case Management System
        </h1>
        
        {/* Dashboard Layout */}
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 mb-8">
          {/* Stats Section */}
          <div className="lg:col-span-3">
            <CaseStats key={refreshKey} />
          </div>
          
          {/* Case Analyzer Section */}
          <div className="lg:col-span-1">
            <CaseAnalyzer onCaseAdded={handleCaseAdded} />
          </div>
          
          {/* Case Table Section */}
          <div className="lg:col-span-2">
            <CaseTable key={refreshKey} />
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
